package forloop5;


public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i=1;i<=7;i++)
{
	for (int j=0;j<i;j++)
	{
		System.out.print("*");
		}
		
		System.out.println("");
	}
for (int i=7;i>=0;i--)
{
	for (int j=0;j<i;j++)
	{
		System.out.print("*");
		}
		
		System.out.println("");
	}


}
}

